#pragma config(Motor,  port1,           rightMotor,    tmotorNormal, openLoop, reversed)
#pragma config(Motor,  port10,           leftMotor,     tmotorNormal, openLoop)
#pragma config(Motor,  port3,           mainarmM,      tmotorServoContinuousRotation, openLoop)
#pragma config(Motor,  port4,           wristM,        tmotorServoContinuousRotation, openLoop)
#pragma config(Motor,  port6,           mobileM,       tmotorNone, openLoop)
#pragma config(Motor,  port7,           clawM,         tmotorServoContinuousRotation, openLoop)

//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
int asd = 0;
int state = 0;
int state2 = 0;
int up = 127;
int non = 0;
int dw = -127;
