task usercontrol()
{
	while(true){

		motor[leftMotor]  = -(vexRT[Ch3]);
		motor[rightMotor] = -(vexRT[Ch2]);

		//Claw Motion
		if (vexRT[Btn6U] == 1)
		{
			motor[clawM] = up;
		}
		else if (vexRT[Btn6D] ==1)
		{
			motor[clawM] = dw;
		}
		else
		{
			motor[clawM] = non;
		}


		//Mobile Motion
		if (vexRT[Btn5U] == 1)
		{
			motor[mobileM] = 60;

		}
		else if (vexRT[Btn5D] ==1)
		{
			motor[mobileM] = -60;
		}
		else
		{
			motor[mobileM] = non;

		}

		// Main Arm State
		
		
		if (vexRT[Btn8U] == !state)
		{
			motor[mainarmM] = dw;			
		}
		
		if (vexRT[Btn8U] == 1)
		{
			motor[mainarmM] = up;
			motor[mainarmM] = dw;			
		}

		
		
		// Wrist Motion
		if (vexRT[Btn8R] == 1)
		{
			motor[wristM] = up;
		}
		else if (vexRT[Btn8L] == 1)
		{
			motor[wristM] = dw;
		}
		else
		{
			motor[wristM] = non;
		}



		// Super Spin
		if (vexRT[Btn7L] == 1)
		{
			motor[rightMotor] = up;
			motor[leftMotor] = dw;

			delay(1100);
			motor[leftMotor] = non;
			motor[rightMotor] = non;
		}
		else if(vexRT[Btn7R] == 1)
		{
			motor[leftMotor] = up;
			motor[rightMotor] = dw;
			delay(1100);
			motor[leftMotor] = non;
			motor[rightMotor] = non;
		}
	}

}
